/*
import Web3 from 'web3';

const web3 = new Web3(window.ethereum.currentProvider);

export default web3;
*/

import Web3 from 'web3';


const options = {
  transactionConfirmationBlocks: 1,
} 

let web3;

if (typeof window !== 'undefined' && typeof window.ethereum !== 'undefined') {
  // We are in the browser and metamask is running.
  web3 = new Web3(window.ethereum.currentProvider, null, options);
  window.ethereum.currentProvider.enable();
} else {

  // We are on the server *OR* the user is not running metamask
  const provider = new Web3.providers.HttpProvider(
    'https://rinkeby.infura.io/v3/45920110e9614f5d9845a12d7cc1e6b4'
  );

  //const provider = new Web3.providers.HttpProvider('HTTP://192.168.1.153:7545');

  web3 = new Web3(provider);
}

export default web3;
