import React, { Component } from 'react';
import { Divider, Header, Icon, Table } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import Queja from '../../bc/queja';

class DetalleQueja extends Component {
    static async getInitialProps(props) {
        const queja = Queja(props.query.address);

        const detalle = await queja.methods.verDetalle().call();

        //return (q.quienReporta,q.nombre,q.correo,q.fecha,q.descripcion);
        return {
            quien: detalle[0],
            nombre: detalle[1],
            correo: detalle[2],
            fecha: detalle[3],
            descripcion: detalle[4]
        };
    }

    render() {
        return (
            <Layout>
                <h3>Detalle de la Queja</h3>
                <React.Fragment>
    <Divider horizontal>
      <Header as='h4'>
        <Icon name='tag' />
        Description
      </Header>
    </Divider>

    <p>
      Doggie treats are good for all times of the year. Proven to be eaten by 99.9% of all dogs
      worldwide.
    </p>

    <Divider horizontal>
      <Header as='h4'>
        <Icon name='bar chart' />
        Specifications
      </Header>
    </Divider>

    <Table definition>
      <Table.Body>
        <Table.Row>
          <Table.Cell width={2}>Size</Table.Cell>
          <Table.Cell>1" x 2"</Table.Cell>
        </Table.Row>
        <Table.Row>
          <Table.Cell>Weight</Table.Cell>
          <Table.Cell>6 ounces</Table.Cell>
        </Table.Row>
        <Table.Row>
          <Table.Cell>Color</Table.Cell>
          <Table.Cell>Yellowish</Table.Cell>
        </Table.Row>
        <Table.Row>
          <Table.Cell>Odor</Table.Cell>
          <Table.Cell>Not Much Usually</Table.Cell>
        </Table.Row>
      </Table.Body>
    </Table>
  </React.Fragment>
            </Layout>
            
        );
    }

}

export default DetalleQueja;
