import web3 from './web3';
//import CAPAchain from './build/contracts/CAPAChain';

const instance = new web3.eth.Contract(
    JSON.parse([
        {
          "constant": true,
          "inputs": [
            {
              "name": "",
              "type": "uint256"
            }
          ],
          "name": "quejasEmitidas",
          "outputs": [
            {
              "name": "",
              "type": "address"
            }
          ],
          "payable": false,
          "stateMutability": "view",
          "type": "function"
        },
        {
          "constant": false,
          "inputs": [
            {
              "name": "descripcion",
              "type": "string"
            }
          ],
          "name": "nuevaQueja",
          "outputs": [],
          "payable": false,
          "stateMutability": "nonpayable",
          "type": "function"
        },
        {
          "constant": true,
          "inputs": [],
          "name": "quejasRegistradas",
          "outputs": [
            {
              "name": "",
              "type": "address[]"
            }
          ],
          "payable": false,
          "stateMutability": "view",
          "type": "function"
        }
      ]),
    '0x3f634B5799D2989865483d460Ca8A1C9acfebD65'
);

export default instance;
